import pygame
import sys
import random
import base64
from pygame.locals import *
from io import BytesIO
from PIL import Image


# Format: "data:image/png;base64,<actual base64 string>"
SMILE_FACE_IMAGE = "data:image/png;base64,"
DEAD_FACE_IMAGE = "data:image/png;base64,"
COOL_FACE_IMAGE = "data:image/png;base64,"
MINE_IMAGE = "data:image/png;base64,"
FLAG_IMAGE = "data:image/png;base64,"


# Initialize pygame
pygame.init()

# Constants for different difficulty levels
DIFFICULTIES = {
    'easy': {'size': 8, 'mines': 10},
    'medium': {'size': 12, 'mines': 20},
    'hard': {'size': 16, 'mines': 40}
}

# Default difficulty (can be changed)
current_difficulty = 'medium'

# Game constants based on difficulty
GRID_SIZE = DIFFICULTIES[current_difficulty]['size']
MINE_COUNT = DIFFICULTIES[current_difficulty]['mines']
CELL_SIZE = 40
HEADER_HEIGHT = 60
WINDOW_WIDTH = GRID_SIZE * CELL_SIZE
WINDOW_HEIGHT = GRID_SIZE * CELL_SIZE + HEADER_HEIGHT
FONT_SIZE = 30

# Colors
WHITE = (255, 255, 255)
BLACK = (0, 0, 0)
GRAY = (200, 200, 200)
DARK_GRAY = (150, 150, 150)
RED = (255, 0, 0)

# Number colors
COLORS = {
    1: (0, 0, 255),
    2: (0, 128, 0),
    3: (255, 0, 0),
    4: (0, 0, 128),
    5: (128, 0, 0),
    6: (0, 128, 128),
    7: (0, 0, 0),
    8: (128, 128, 128)
}


def load_base64_image(base64_string, size=(GRID_SIZE, GRID_SIZE)):
    """Convert to BMP to avoid libpng warnings."""
    image_data = base64.b64decode(base64_string)
    
    # Load with Pillow, convert to BMP (no libpng involved)
    img = Image.open(BytesIO(image_data))
    bmp_buffer = BytesIO()
    img.save(bmp_buffer, format="BMP")
    bmp_buffer.seek(0)
    
    # Pygame loads BMP without libpng
    image = pygame.image.load(bmp_buffer)
    return pygame.transform.scale(image, size)

class ImageCache:
    def __init__(self):
        self.cache = {}
    
    def get_image(self, base64_str, size):
        if base64_str in self.cache:
            return self.cache[base64_str]
        
        try:
            # Remove the data URL prefix if present
            if base64_str.startswith("data:image/"):
                base64_str = base64_str.split(",")[1]
            
            # Use the load_base64_image function
            image_surface = load_base64_image(base64_str, (size, size))
            self.cache[base64_str] = image_surface
            return image_surface
        except:
            # Fallback: create a colored rectangle
            print(f"Failed to load image from Base64: {base64_str[:30]}...")
            fallback = pygame.Surface((size, size))
            fallback.fill(RED)
            return fallback

# Initialize image cache
image_cache = ImageCache()

# Set up the window
window = pygame.display.set_mode((WINDOW_WIDTH, WINDOW_HEIGHT), pygame.RESIZABLE)
pygame.display.set_caption('Minesweeper with Images')

# Load fonts
try:
    number_font = pygame.font.SysFont('Arial', FONT_SIZE - 10, bold=True)
    header_font = pygame.font.SysFont('Arial', 30)
    menu_font = pygame.font.SysFont('Arial', 20)
except:
    print("Warning: Using fallback fonts.")
    number_font = pygame.font.SysFont('Arial', FONT_SIZE - 10, bold=True)
    header_font = pygame.font.SysFont('Arial', 30)
    menu_font = pygame.font.SysFont('Arial', 20)

class MinesweeperGame:
    def __init__(self):
        self.set_difficulty(current_difficulty)
    
    def set_difficulty(self, difficulty):
        global GRID_SIZE, MINE_COUNT, CELL_SIZE, WINDOW_WIDTH, WINDOW_HEIGHT
        
        self.difficulty = difficulty
        GRID_SIZE = DIFFICULTIES[difficulty]['size']
        MINE_COUNT = DIFFICULTIES[difficulty]['mines']
        
        # Adjust cell size based on grid size to fit the screen
        max_cell_size = min(800 // GRID_SIZE, 600 // GRID_SIZE)
        CELL_SIZE = min(40, max_cell_size)  # Cap at 40 but don't go too small
        
        WINDOW_WIDTH = GRID_SIZE * CELL_SIZE
        WINDOW_HEIGHT = GRID_SIZE * CELL_SIZE + HEADER_HEIGHT
        
        # Resize the window
        pygame.display.set_mode((WINDOW_WIDTH, WINDOW_HEIGHT), pygame.RESIZABLE)
        
        self.reset_game()
    
    def reset_game(self):
        self.board = [[0 for _ in range(GRID_SIZE)] for _ in range(GRID_SIZE)]
        self.revealed = [[False for _ in range(GRID_SIZE)] for _ in range(GRID_SIZE)]
        self.flagged = [[False for _ in range(GRID_SIZE)] for _ in range(GRID_SIZE)]
        self.game_over = False
        self.win = False
        self.mines_placed = False
        self.first_click = True
        self.mines_left = MINE_COUNT
        
        # Initialize header text
        self.update_header()
    
    def update_header(self):
        status_image = SMILE_FACE_IMAGE  # Default smile face
        if self.game_over:
            status_image = DEAD_FACE_IMAGE if not self.win else COOL_FACE_IMAGE
        
        self.status_img = image_cache.get_image(status_image, FONT_SIZE)
        self.header_text = f"   Mines: {self.mines_left}"
        self.difficulty_text = f"Difficulty: {self.difficulty.capitalize()}"
    
    def place_mines(self, first_click_row, first_click_col):
        # Place mines randomly, avoiding the first click position and adjacent cells
        safe_zone = set()
        for r in range(max(0, first_click_row-1), min(GRID_SIZE, first_click_row+2)):
            for c in range(max(0, first_click_col-1), min(GRID_SIZE, first_click_col+2)):
                safe_zone.add((r, c))
        
        mines_placed = 0
        while mines_placed < MINE_COUNT:
            row, col = random.randint(0, GRID_SIZE-1), random.randint(0, GRID_SIZE-1)
            if (row, col) not in safe_zone and self.board[row][col] != -1:
                self.board[row][col] = -1  # -1 represents a mine
                mines_placed += 1
        
        # Calculate adjacent mine counts
        for row in range(GRID_SIZE):
            for col in range(GRID_SIZE):
                if self.board[row][col] != -1:
                    count = 0
                    for r in range(max(0, row-1), min(GRID_SIZE, row+2)):
                        for c in range(max(0, col-1), min(GRID_SIZE, col+2)):
                            if self.board[r][c] == -1:
                                count += 1
                    self.board[row][col] = count
        
        self.mines_placed = True
    
    def reveal_cell(self, row, col):
        # Don't allow any actions if game is already over
        if self.game_over:
            return False
            
        if not (0 <= row < GRID_SIZE and 0 <= col < GRID_SIZE):
            return False
            
        if self.revealed[row][col] or self.flagged[row][col]:
            return False
            
        if self.first_click:
            self.place_mines(row, col)
            self.first_click = False
            
        self.revealed[row][col] = True
        
        if self.board[row][col] == -1:  # Clicked on a mine
            self.game_over = True
            self.win = False
            self.reveal_all_mines()
            self.update_header()
            return True
            
        if self.board[row][col] == 0:  # Empty cell, reveal adjacent
            for r in range(max(0, row-1), min(GRID_SIZE, row+2)):
                for c in range(max(0, col-1), min(GRID_SIZE, col+2)):
                    if (r != row or c != col) and not self.revealed[r][c]:
                        self.reveal_cell(r, c)
        
        self.check_win()
        return True
    
    def toggle_flag(self, row, col):
        if not (0 <= row < GRID_SIZE and 0 <= col < GRID_SIZE) or self.revealed[row][col]:
            return
        
        self.flagged[row][col] = not self.flagged[row][col]
        if self.flagged[row][col]:
            self.mines_left -= 1
        else:
            self.mines_left += 1
        
        self.update_header()
        self.check_win()
    
    def check_win(self):
        # Check if all non-mine cells are revealed
        for row in range(GRID_SIZE):
            for col in range(GRID_SIZE):
                if self.board[row][col] != -1 and not self.revealed[row][col]:
                    return
        
        self.game_over = True
        self.win = True
        self.update_header()
    
    def reveal_all_mines(self):
        for row in range(GRID_SIZE):
            for col in range(GRID_SIZE):
                if self.board[row][col] == -1:
                    self.revealed[row][col] = True
    
    def draw(self, surface):
        # Draw header
        header_rect = pygame.Rect(0, 0, WINDOW_WIDTH, HEADER_HEIGHT)
        pygame.draw.rect(surface, GRAY, header_rect)
        
        # Draw difficulty text - positioned at the left
        difficulty_surface = menu_font.render(self.difficulty_text, True, BLACK)
        difficulty_rect = difficulty_surface.get_rect(midleft=(10, HEADER_HEIGHT//2))
        surface.blit(difficulty_surface, difficulty_rect)
        
        # Draw mines count text - positioned to the right
        header_surface = header_font.render(self.header_text, True, BLACK)
        header_text_rect = header_surface.get_rect(midright=(WINDOW_WIDTH - 10, HEADER_HEIGHT//2))
        surface.blit(header_surface, header_text_rect)
        
        # Draw restart button if game is over (centered in header)
        if self.game_over:
            button_width = 120
            button_height = 40
            restart_button = pygame.Rect(
                (WINDOW_WIDTH - button_width) // 2,  # Centered horizontally
                (HEADER_HEIGHT - button_height) // 2,  # Centered vertically
                button_width,
                button_height
            )
            pygame.draw.rect(surface, (200, 50, 50), restart_button)
            pygame.draw.rect(surface, BLACK, restart_button, 2)
            restart_text = menu_font.render("Restart", True, WHITE)
            restart_text_rect = restart_text.get_rect(center=restart_button.center)
            surface.blit(restart_text, restart_text_rect)
        else:
            # Draw status image (smiley face) - centered horizontally when game is not over
            status_rect = self.status_img.get_rect(center=(WINDOW_WIDTH//2, HEADER_HEIGHT//2))
            surface.blit(self.status_img, status_rect)
        
        # Draw grid
        for row in range(GRID_SIZE):
            for col in range(GRID_SIZE):
                rect = pygame.Rect(
                    col * CELL_SIZE,
                    row * CELL_SIZE + HEADER_HEIGHT,
                    CELL_SIZE,
                    CELL_SIZE
                )
                
                # Draw cell background
                if self.revealed[row][col]:
                    pygame.draw.rect(surface, WHITE, rect)
                    pygame.draw.rect(surface, DARK_GRAY, rect, 1)
                    
                    # Draw mine or number
                    if self.board[row][col] == -1:  # Mine
                        mine_img = image_cache.get_image(MINE_IMAGE, CELL_SIZE - 10)
                        img_rect = mine_img.get_rect(center=rect.center)
                        surface.blit(mine_img, img_rect)
                    elif self.board[row][col] > 0:  # Number
                        number_text = number_font.render(
                            str(self.board[row][col]), 
                            True, 
                            COLORS[self.board[row][col]]
                        )
                        text_rect = number_text.get_rect(center=rect.center)
                        surface.blit(number_text, text_rect)
                else:
                    pygame.draw.rect(surface, GRAY, rect)
                    pygame.draw.rect(surface, DARK_GRAY, rect, 1)
                    
                    # Draw flag
                    if self.flagged[row][col]:
                        flag_img = image_cache.get_image(FLAG_IMAGE, CELL_SIZE - 10)
                        img_rect = flag_img.get_rect(center=rect.center)
                        surface.blit(flag_img, img_rect)

def draw_menu(surface, game):
    # Draw difficulty selection menu
    menu_rect = pygame.Rect(WINDOW_WIDTH//2 - 100, HEADER_HEIGHT + 50, 200, 150)
    pygame.draw.rect(surface, WHITE, menu_rect)
    pygame.draw.rect(surface, BLACK, menu_rect, 2)
    
    title = menu_font.render("Select Difficulty:", True, BLACK)
    surface.blit(title, (menu_rect.centerx - title.get_width()//2, menu_rect.y + 10))
    
    easy_rect = pygame.Rect(menu_rect.x + 20, menu_rect.y + 40, 160, 30)
    med_rect = pygame.Rect(menu_rect.x + 20, menu_rect.y + 80, 160, 30)
    hard_rect = pygame.Rect(menu_rect.x + 20, menu_rect.y + 120, 160, 30)
    
    # Draw buttons
    pygame.draw.rect(surface, GRAY, easy_rect)
    pygame.draw.rect(surface, GRAY, med_rect)
    pygame.draw.rect(surface, GRAY, hard_rect)
    
    pygame.draw.rect(surface, BLACK, easy_rect, 2)
    pygame.draw.rect(surface, BLACK, med_rect, 2)
    pygame.draw.rect(surface, BLACK, hard_rect, 2)
    
    # Draw button text
    easy_text = menu_font.render("Easy", True, BLACK)
    med_text = menu_font.render("Medium", True, BLACK)
    hard_text = menu_font.render("Hard", True, BLACK)
    
    surface.blit(easy_text, (easy_rect.centerx - easy_text.get_width()//2, easy_rect.centery - easy_text.get_height()//2))
    surface.blit(med_text, (med_rect.centerx - med_text.get_width()//2, med_rect.centery - med_text.get_height()//2))
    surface.blit(hard_text, (hard_rect.centerx - hard_text.get_width()//2, hard_rect.centery - hard_text.get_height()//2))
    
    return easy_rect, med_rect, hard_rect

def main():
    game = MinesweeperGame()
    clock = pygame.time.Clock()
    show_menu = False
    
    while True:
        for event in pygame.event.get():
            if event.type == QUIT:
                pygame.quit()
                sys.exit()
            
            elif event.type == MOUSEBUTTONDOWN:
                x, y = event.pos
    
                # Check if click is in header
                if y < HEADER_HEIGHT:
                    if x < 150:  # Clicked on difficulty text
                        show_menu = not show_menu
                    elif game.game_over:
                        # Check if click is on restart button
                        button_width = 120
                        button_height = 40
                        button_x = (WINDOW_WIDTH - button_width) // 2
                        button_y = (HEADER_HEIGHT - button_height) // 2
                        
                        if (button_x <= x <= button_x + button_width and 
                            button_y <= y <= button_y + button_height):
                            game.reset_game()
                    else:  # Clicked on smiley or mines count
                        game.reset_game()
                    continue
                
                if show_menu:
                    # Check difficulty selection
                    easy_rect, med_rect, hard_rect = draw_menu(window, game)
                    if easy_rect.collidepoint(x, y):
                        game.set_difficulty('easy')
                        show_menu = False
                    elif med_rect.collidepoint(x, y):
                        game.set_difficulty('medium')
                        show_menu = False
                    elif hard_rect.collidepoint(x, y):
                        game.set_difficulty('hard')
                        show_menu = False
                    continue
                
                # Convert to grid coordinates
                row = (y - HEADER_HEIGHT) // CELL_SIZE
                col = x // CELL_SIZE
                
                if event.button == 1:  # Left click
                    game.reveal_cell(row, col)
                elif event.button == 3:  # Right click
                    game.toggle_flag(row, col)
        
        # Draw everything
        window.fill(WHITE)
        game.draw(window)
        
        if show_menu:
            draw_menu(window, game)
        
        pygame.display.update()
        clock.tick(30)

if __name__ == "__main__":
    main()
